@title BalCam@param a interval (sec)@default a 12@param d initDelay (sec)@default d 20@param c zoomShot (frames)@default 10@param n fileNum@default n 1if a<5 then a=5a=a*1000if d<1 then d=1d=d*1000if(c<0) then c=0p = cset_zoom_speed 95Z = get_zoom_stepsprint "Script will continue"print "until interrupted (shutt)"print "while this script may be"print "interrupt-friendly, you"print "have been warned."sleep dclsprint_screen nprint get_time 4;"/";get_time 3;"/";get_time 5;" ";get_time 2;":";get_time 1;":";get_time 0print " "print "Time,Optics Temp,CCD Temp"o = get_day_seconds:main    gosub "getData"    gosub "takePic"    set_backlight 0    sleep a    rem set_backlight 1goto "main":takePic    set_zoom 0    do        get_zoom z    until z = 0    shoot    p = p - 1    if (p < 0) then p = c    sleep 500    return:takeZoomPic    set_zoom (Z-2)    do        get_zoom z	print "Z=";Z;"z=";z    until z = (Z-2)    shoot    p = c    sleep 500    return:getData    print (get_day_seconds - o);",";get_temperature 0;",";get_temperature 1    return:restore    set_backlight 1    end